        public void Draw()
        {
            for (int i = 0; i < map.GetLength(0); i++)
                for (int j = 0; j < map.GetLength(1); j++)
                {
                    Console.SetCursorPosition(j, i);
                    Console.Write(map[i, j]);
                }
        }

        public bool IsWall(int x, int y)
        {
            switch (map[y, x])
            {
                case '`':
                case ',':
                case '*':
                    return false;
                case '^':
                case '~':
                    return true;
            }
            return true;
        }
